import React, {useState , useEffect} from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom'
const AllCourses = () => {
    const [data , setData] = useState([])
    const [searchTerm, setSearchTerm] = useState('')
    const [categoryId, setCategoryId] = useState('')
    useEffect(()=>{
        axios.get('http://localhost:5000/course/courses')
        .then(res => setData(res.data))
        .catch(error => console.log(error))
    },[])

    useEffect(()=>{
        let url = 'http://localhost:5000/course/courses';
        if (searchTerm) url += `?name=${searchTerm}`;
        if (categoryId) url = `http://localhost:5000/category/categories/${categoryId}`;
        axios.get(url)
        .then(res => setData(res.data))
        .catch(error => console.log(error))
    },[searchTerm, categoryId])


    const handleDelete = (id) => {
        axios.delete(`http://localhost:5000/course/delete-course/${id}`)
        .then(res => {
            window.reload()
        })
        .catch(error => console.log(error))
    }
  return (
    <div className='d-flex vh-100 bg-primary justify-content-center align-items-center'>
        <section className='w-50 bg-white rounded p-3'>
            <h2>Courses</h2>
            <div className="d-flex justify-content-between">
                <Link to="/create-course" className='btn btn-success'>Create Course</Link>
                <Link to="/all-categories" className='btn btn-success'>Go to Categories</Link>
            </div>

            <input type="text" placeholder="Search by name..." onChange={e => setSearchTerm(e.target.value)} />
                <input type="text" placeholder="Search by category ID..." onChange={e => setCategoryId(e.target.value)} />
            
            <table className='table'>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Category_ID</th>
                        <th>Status</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        data.map((course , index) => (
                            <tr key={index}>
                                <td>{course.id}</td>
                                <td>{course.name}</td>
                                <td>{course.category_id}</td>
                                <td>{course.status}</td>
                                <td>
                                    <Link to={`/course/${course.id}`} className='btn btn-sm btn-info'>View More</Link>
                                    <Link to={`/edit-course/${course.id}`} className='btn btn-sm btn-primary mx-2'>Edit</Link>
                                    <button onClick={() => handleDelete(course.id)} className='btn btn-sm btn-danger'>Delete</button>
                                </td>
                            </tr>
                        ))
                    }
                </tbody>
            </table>
        </section>
    </div>
  )
}

export default AllCourses
