import React , {useEffect , useState} from 'react'
import axios from 'axios'
const SingleCourse = ({ onClose, courseId }) => {
  const [course, setCourse] = useState({});

  useEffect(() => {
      axios.get(`http://localhost:5000/course/course/${courseId}`)
          .then(res => {
              console.log(res.data);
              setCourse(res.data);
          })
          .catch(err => console.log(err));
  }, [courseId]);

  return (
      <div className='modal-overlay'>
          <div className='modal'>
              <button className='close-btn' onClick={onClose}>Close</button>
              <h3>ID: {course.id}</h3>
              <h3>Name: {course.name}</h3>
              <h3>Description: {course.description}</h3>
              <h3>Category_id: {course.category_id}</h3>
              <h3>Status: {course.status}</h3>
              <h3>Teacher: {course.teacher}</h3>
              <h3>Video_link: {course.video_link}</h3>
          </div>
      </div>
  );
};

export default SingleCourse
