import React , {useEffect , useState} from 'react'
import axios from 'axios'
import {useParams}  from 'react-router-dom'
const SingleCourse = ({closeModal}) => {
    const [course , setCourse] = useState([])
    const { id } = useParams()

    useEffect(() => {
        axios.get(`http://localhost:5000/course/course/${id}`)
        .then(res => {
            console.log(res.data)
            setCourse(res.data)
        })
        .catch(err => console.log(err))
    }, [])

  return (
    <div className='absolute vh-100 bg-primary justify-content-center align-items-center'>
        <div className='w-50 bg-white rounded p-3'>
                <h3>ID : {course.id}</h3>
                <h3>Name : {course.name}</h3>
                <h3>Description : {course.description}</h3>
                <h3>Category_id : {course.category_id}</h3>
                <h3>Status : {course.status}</h3>
                <h3>Teacher : {course.teacher}</h3>
                <h3>Video_link : {course.video_link}</h3>
        </div>
    </div>
  )
}

export default SingleCourse
