import React , { useEffect , useState} from 'react'
import axios from 'axios'
import { useParams , useNavigate } from 'react-router-dom'

const EditCourse = () => {
    const [values , setValues] = useState({
        name : '',
        description : '',
        category_id : '',
        status : '',
        teacher : '',
        video_link : ''
    })

    const { id } = useParams()

    const navigate = useNavigate()

    useEffect(() => {
        axios.get(`http://localhost:5000/course/${id}`)
        .then(res => {
            console.log(res.data)
            setValues({...values , 
                name: res.data[0].name ,
                description: res.data[0].description , 
                category_id: res.data[0].category_id ,
                status: res.data[0].status, 
                teacher: res.data[0].teacher ,
                video_link: res.data[0].video_link 
            })
        })
        .catch(err => console.log(err))
    }, [])


    const handleEdit = (e) => {
        e.preventDefault();
        axios.put(`http://localhost:5000/edit-course/${id}` , values)
        .then(res => {
            console.log(res)
            navigate("/")
        })
        .catch(err => console.log(err))
    }
  return (
    <div className='d-flex vh-100 bg-primary justify-content-center align-items-center'>
        <section className='w-50 bg-white rounded p-3'>
            <form onSubmit={handleEdit}>
                <h2>Edit Course</h2>
                <div>
                    <label htmlFor=''>Name</label>
                    <input type='text' placeholder='Enter your name' className='form-control' 
                    value={values.name}
                    onChange={e => setValues({...values , name : e.target.value})}
                    />
                </div>
                <div>
                    <label htmlFor=''>Description</label>
                    <textarea type='text' placeholder='Enter your name' className='form-control' 
                    value={values.description}
                    onChange={e => setValues({...values , description : e.target.value})}
                    />
                </div>
                <button className='btn btn-success'>Submit</button>
            </form>
        </section>
    </div>
  )
}

export default EditCourse
